      // Buttons
      this.controls.btns.forEach(({ ref }, k) => {
        setTimeout(() => {
          this.controls.btns[k].value = 'enabled';
        }, (k + 1) * 50);
        setTimeout(() => {
          this.controls.btns[k].value = 'disabled';
        }, 700);
      });

      setTimeout(() => {
        // Letters
        SVG(this.screen).fill(colors.screen.enabled);
        this.letters.forEach((el, k) => {
          setTimeout(() => {
            SVG(el).fill({ color: colors.screen.text, opacity: 1 });
            setTimeout(() => {
              SVG(el).fill({ color: colors.screen.text, opacity: 0 });
            }, k + 1 + 1300);
          }, (k + 1) * 150);
        });

        // Sliders
        this.controls.sliders.forEach(({ ref }, k) => {
          this.controls.sliders[k].value = this.controls.sliders[k].max;
          setTimeout(() => {
            this.controls.sliders[k].value = this.controls.sliders[k].min;
          }, 500);
        });

        // Pads
        this.controls.pads.forEach(({ ref }, k) => {
          setTimeout(() => {
            this.controls.pads[k].value = 'enabled';
            setTimeout(() => {
              this.controls.pads[k].value = 'active';
              setTimeout(() => {
                this.controls.pads[k].value = 'disabled';
              }, k + 1 + 50);
            }, k + 1 + 50);
          }, (k + 1) * 15);
        });

        setTimeout(() => {
          this.controls.pads.reverse().forEach(({ ref }, k) => {
            setTimeout(() => {
              this.controls.pads[k].value = 'enabled';
              setTimeout(() => {
                this.controls.pads[k].value = 'active';
                setTimeout(() => {
                  this.controls.pads[k].value = 'disabled';
